import{e as l}from"./app.515328db.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";var e="/assets/a.ae1b7da9.jpg";const a={},r=l(`<h1 id="\u8BA1\u7B97\u673A\u7F51\u7EDC" tabindex="-1"><a class="header-anchor" href="#\u8BA1\u7B97\u673A\u7F51\u7EDC" aria-hidden="true">#</a> \u8BA1\u7B97\u673A\u7F51\u7EDC</h1><h2 id="\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a> \u6982\u8FF0</h2><h2 id="\u5E94\u7528\u5C42" tabindex="-1"><a class="header-anchor" href="#\u5E94\u7528\u5C42" aria-hidden="true">#</a> \u5E94\u7528\u5C42</h2><h2 id="_3-\u4F20\u8F93\u5C42" tabindex="-1"><a class="header-anchor" href="#_3-\u4F20\u8F93\u5C42" aria-hidden="true">#</a> 3. \u4F20\u8F93\u5C42</h2><h3 id="_1-\u4F20\u8F93\u5C42\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#_1-\u4F20\u8F93\u5C42\u670D\u52A1" aria-hidden="true">#</a> 1. \u4F20\u8F93\u5C42\u670D\u52A1</h3><ol><li>\u4F20\u8F93\u5C42\u548C\u7F51\u7EDC\u5C42\u7684\u533A\u522B</li></ol><ul><li>\u7F51\u7EDC\u5C42\uFF1A\u4E3B\u673A\u95F4\u7684\u903B\u8F91\u901A\u4FE1</li><li>\u4F20\u8F93\u5C42\uFF1A\u8FDB\u7A0B\u95F4\u7684\u903B\u8F91\u901A\u4FE1</li></ul><p>\u4F20\u8F93\u5C42\u7684\u670D\u52A1 = \u4F20\u8F93\u5C42\u7684\u534F\u8BAE + \u7F51\u7EDC\u5C42\u7684\u670D\u52A1</p><p>\u4E0D\u540C\u4E3B\u673A\u8FDB\u7A0B\u95F4\u6570\u636E\u4F20\u8F93 = \u4F20\u8F93\u5C42\u7684\u534F\u8BAE + \u8FDB\u7A0B\u6240\u5728\u4E3B\u673A\u95F4\u6570\u636E\u4F20\u8F93</p><p>\u53D1\u9001\u7AEF\uFF1A</p><ul><li>\u63A5\u6536\u5E94\u7528\u5C42\u7684\u6D88\u606F</li><li>\u8BBE\u7F6E\u62A5\u6587\u5934\u90E8\u5B57\u6BB5\u7684\u503C</li><li>\u521B\u5EFA\u62A5\u6587\u6BB5</li><li>\u628A\u62A5\u6587\u7AEF\u4F20\u9012\u7ED9\u5BF9\u5E94 IP</li></ul><p>\u63A5\u6536\u7AEF\uFF1A</p><ul><li>\u4ECE\u67D0 IP \u63A5\u6536 \u62A5\u6587\u6BB5</li><li>\u68C0\u67E5 header \u7684\u503C</li><li>\u89E3\u6790\u5E94\u7528\u5C42\u6D88\u606F</li><li>\u901A\u8FC7 socket \u628A\u6570\u636E\u591A\u8DEF\u5206\u89E3\u5230\u5E94\u7528</li></ul><ol start="2"><li>\u4E24\u4E2A\u4F20\u8F93\u5C42\u534F\u8BAE</li></ol><p>TCP(Transmission Control Protocol)\uFF1A</p><ul><li>\u53EF\u9760\u4F20\u8F93 (reliable, in-order delivery)</li><li>\u62E5\u585E\u63A7\u5236 (congestion control)</li><li>\u6D41\u91CF\u63A7\u5236 (flow control)\uFF1A\u786E\u4FDD\u53D1\u9001\u7AEF\u4E0D\u4F1A\u53D1\u9001\u8FC7\u591A\u6570\u636E\u5BFC\u81F4\u63A5\u6536\u7AEF buffer \u6EA2\u51FA\u800C\u4E22\u5305\u7684\u673A\u5236</li><li>\u9762\u5411\u5EFA\u7ACB</li></ul><p>UDP(User Datagram Protocol)\uFF1A</p><ul><li>\u4E0D\u53EF\u9760\u4F20\u8F93 (unreliable, unordered delivery)</li><li>\u5C3D\u529B\u800C\u4E3A (no-frills extension of &quot;best-effort&quot; IP)</li></ul><h3 id="_2-\u591A\u8DEF\u590D\u7528-\u591A\u8DEF\u5206\u89E3" tabindex="-1"><a class="header-anchor" href="#_2-\u591A\u8DEF\u590D\u7528-\u591A\u8DEF\u5206\u89E3" aria-hidden="true">#</a> 2. \u591A\u8DEF\u590D\u7528 / \u591A\u8DEF\u5206\u89E3</h3><p><em>Multiplexing/demultiplexing</em></p><ul><li>\u521B\u5EFA socket \u65F6\u9700\u8981\u6307\u5B9A\u672C\u673A\u7AEF\u53E3\u53F7</li><li>\u8FDE\u63A5 UDP \u65F6\u9700\u8981\u6307\u5B9A\u76EE\u7684\u4E3B\u673A\u7684 IP \u548C \u7AEF\u53E3\u53F7</li><li>\u5F53\u63A5\u6536\u7AEF\u63A5\u6536\u5230 UDP \u62A5\u6587\u540E\u4F1A\u68C0\u67E5\u76EE\u7684\u7AEF\u53E3\uFF0C\u7136\u53E3\u6295\u9012\u5230\u5BF9\u5E94\u7684\u8FDB\u7A0B</li></ul><p>TCP \u9762\u5411\u8FDE\u63A5\uFF0CTCP socket \u5B9A\u4E49\u4E86\u4E00\u4E2A\u56DB\u5143\u7EC4</p><p>\u603B\u7ED3</p><ul><li>UDP \u591A\u8DEF\u5206\u89E3\u53EA\u4F7F\u7528\u7AEF\u53E3\u53F7</li><li>TCP \u591A\u8DEF\u5206\u89E3\u4F7F\u7528 \u6E90\u548C\u76EE\u5730\u7684 IP \u548C \u7AEF\u53E3</li><li>\u591A\u8DEF\u590D\u7528\u548C\u591A\u8DEF\u5206\u89E3\u53D1\u751F\u5728\u6240\u6709\u5C42</li></ul><h3 id="_3-\u65E0\u8FDE\u63A5\u4F20\u8F93-udp" tabindex="-1"><a class="header-anchor" href="#_3-\u65E0\u8FDE\u63A5\u4F20\u8F93-udp" aria-hidden="true">#</a> 3. \u65E0\u8FDE\u63A5\u4F20\u8F93 UDP</h3><h4 id="udp-\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#udp-\u7279\u70B9" aria-hidden="true">#</a> UDP \u7279\u70B9</h4><ol><li><p>UDP \u662F\u4E00\u79CD\u5C3D\u529B\u800C\u4E3A\u7684\u534F\u8BAE</p><ul><li>UDP \u62A5\u6587\u6BB5\u53EF\u80FD\u4F1A\u4E22\u5931</li><li>\u4E5F\u53EF\u80FD\u4F1A\u4E71\u5E8F</li></ul></li><li><p>\u65E0\u8FDE\u63A5</p><ul><li>UDP \u53D1\u9001\u7AEF\u548C\u63A5\u6536\u7AEF\u4E0D\u9700\u8981\u63E1\u624B</li><li>\u6BCF\u4E00\u4E2A UDP \u6570\u636E\u6BB5\u90FD\u662F\u72EC\u7ACB\u7684\uFF0C\u4E0A\u4E0B\u53EF\u80FD\u6CA1\u6709\u5173\u7CFB</li><li>\u53EA\u6709 checkout \u7528\u4E8E\u9519\u8BEF\u68C0\u9A8C</li></ul></li></ol><h4 id="\u4E3A\u4EC0\u4E48\u9700\u8981-udp" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u9700\u8981-udp" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u9700\u8981 UDP\uFF1F</h4><ol><li>\u65E0\u8FDE\u63A5\u7B80\u5386\uFF08\u53EF\u80FD\u4F1A\u589E\u52A0\u5EF6\u8FDF\uFF09</li><li>\u7B80\u5355\uFF0C\u53D1\u9001\u7AEF\u548C\u63A5\u6536\u7AEF\u4E0D\u9700\u8981\u4FDD\u5B58\u8FDE\u63A5\u72B6\u6001</li><li>\u6CA1\u6709\u62E5\u585E\u63A7\u5236\uFF0CUDP \u4F1A\u5C3D\u53EF\u80FD\u5FEB\u7684\u9001\u8FBE</li></ol><p>\u5E94\u7528\u573A\u666F\uFF1A</p><p>\u6D41\u5A92\u4F53 app</p><ul><li>\u4F4E\u5EF6\u8FDF</li><li>\u901F\u5EA6\u654F\u611F</li></ul><p>\u5982\u4F55\u5728 UDP \u4E0A\u4E2A\u6784\u5EFA\u53EF\u9760\u4F20\u8F93\uFF1A\u5728\u5E94\u7528\u5C42\u4E0A\u6784\u5EFA\u53EF\u9760\u6027</p><h4 id="udp-\u62A5\u6587\u6BB5\u683C\u5F0F" tabindex="-1"><a class="header-anchor" href="#udp-\u62A5\u6587\u6BB5\u683C\u5F0F" aria-hidden="true">#</a> UDP \u62A5\u6587\u6BB5\u683C\u5F0F</h4><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>+---------+---------+
| \u6E90\u7AEF\u53E3   | \u76EE\u7684\u7AEF\u53E3 |
+---------+---------+
| \u957F\u5EA6     | \u6821\u9A8C\u548C   |
+---------+---------+
| \u5E94\u7528\u6570\u636E\uFF08\u6D88\u606F\uFF09     |
|                   |
+-------------------+
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><ul><li>\u957F\u5EA6\uFF1AUDP \u62A5\u6587\u6BB5\u7684\u957F\u5EA6\uFF08\u5305\u542B header\uFF09</li><li>\u6570\u636E\uFF1A\u4E0A\u5C42\u534F\u8BAE\u6570\u636E\uFF0C\u4F8B\u5982 DNS\uFF0CSNMP</li><li>checksum\uFF1A\u9519\u8BEF\u68C0\u6D4B\uFF08\u6570\u5B66\u624B\u6BB5\u7684\u5197\u4F59\uFF09</li></ul><h4 id="checksum-\u8BA1\u7B97\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#checksum-\u8BA1\u7B97\u65B9\u6CD5" aria-hidden="true">#</a> checksum \u8BA1\u7B97\u65B9\u6CD5</h4><p>\u5C06\u6240\u6709\u6570\u636E\u6C42\u548C</p><ul><li>\u5047\u5982\u53D1\u9001 5 bit \u6570\u636E (7, 11, 12, 0, 6), \u5B9E\u9645\u4F1A\u53D1\u9001 (7, 11, 12, 0, 6, 36)</li><li>\u63A5\u6536\u65B9\u91CD\u65B0\u8BA1\u7B97\u6570\u5B57\u4E4B\u548C\uFF0C\u5E76\u4E0E\u6536\u5230\u7684\u6BD4\u8F83</li><li>\u53D1\u9001\u65B9\u6CD5\u9001\u7684\u662F\u5B9E\u9645\u6570\u636E\u7684\u53CD\u7801 (-36), \u88AB\u79F0\u4E3A checkout</li></ul><p>\u95EE\u9898</p><p>\u5982\u4F55\u7528 4 bit \u8868\u793A 36 \u548C -36\uFF1F</p><p>\u7B54\uFF1A1s \u8865\u8FD0\u7B97</p><ul><li><code>36 = 0001 0100</code></li><li><code>0100 + 0100 = 0110 = 6</code></li><li><code>+6 = 0100 -6 = 1001 \u53D6\u53CD\u5F97\u5230 -6</code></li><li><code>-36 \u53EF\u4EE5\u7528 1001(9) \u8868\u793A</code></li></ul><h3 id="_4-\u53EF\u9760\u4F20\u8F93" tabindex="-1"><a class="header-anchor" href="#_4-\u53EF\u9760\u4F20\u8F93" aria-hidden="true">#</a> 4. \u53EF\u9760\u4F20\u8F93</h3><h4 id="rdt" tabindex="-1"><a class="header-anchor" href="#rdt" aria-hidden="true">#</a> rdt</h4><p>Rdt1.0:</p><ol><li>\u4E0B\u5C42\u7684 channel \u662F\u53EF\u9760\u7684 <ul><li>\u4E0D\u4F1A\u53D1\u751F\u6BD4\u7279\u7FFB\u8F6C</li><li>\u4E0D\u4F1A\u4E22\u5305</li></ul></li><li>\u4E3A sender\uFF0Creceiver \u8BBE\u7F6E\u4E0D\u540C\u7684 FSM <ul><li>sender \u548C receiver \u5206\u522B\u4ECE\u4E0B\u5C42\u7684 channel \u53D1\u9001\u548C\u63A5\u6536\u6570\u636E</li></ul></li></ol><p>Rdt2.0:</p><ul><li>\u53D1\u9001\u7AEF\u53D1\u9001\u6570\u636E</li><li>\u63A5\u6536\u7AEF\u63A5\u6536\u6570\u636E\uFF0C\u5982\u679C\u63A5\u6536\u5230\u7684\u6570\u636E\u6B63\u786E\uFF0C\u8FD4\u56DE ACK\uFF0C\u5982\u679C\u6570\u636E\u9519\u8BEF\uFF0C\u8FD4\u56DE NAK\u3002</li><li>\u53D1\u9001\u7AEF\u6839\u636E\u6536\u5230\u7684\u662F ACK \u8FD8\u662F NAK\uFF0C\u9009\u62E9\u91CD\u4F20</li></ul><p>\u95EE\u9898\uFF1A</p><ol><li>\u51FA\u73B0\u6BD4\u7279\u7FFB\u8F6C <ul><li>\u4F7F\u7528 checksum \u68C0\u6D4B\u9519\u8BEF</li><li>ack/nak \u786E\u8BA4</li><li>\u91CD\u4F20</li></ul></li><li>\u5982\u4F55\u4ECE\u9519\u8BEF\u6062\u590D\uFF1A <ul><li>\u81EA\u52A8\u8BF7\u6C42\u91CD\u4F20</li></ul></li><li>\u65B0\u7684\u72B6\u6001\u673A <ul><li>sender \u4F7F\u7528 udt_send \u53D1\u9001\u6570\u636E\uFF0C\u53D1\u9001\u5B8C\u6BD5\u540E\u7B49\u5F85\u63A5\u6536 ACK\u3002</li><li>\u5982\u679C\u63A5\u6536\u5230\u4E86 ACK\uFF0C\u5C31\u662F\u6210\u529F\u3002</li><li>\u5982\u679C\u63A5\u6536\u5230\u4E86 NAK\uFF0C\u5C31\u4F1A\u91CD\u4F20\u3002</li></ul></li></ol><p>\u7F3A\u70B9\uFF1A</p><ol><li>\u5982\u679C\u63A5\u6536\u65B9\u6210\u529F\u63A5\u6536\uFF0C\u4F46\u662F\u8FD4\u56DE\u5931\u8D25\uFF0C\u4F1A\u5BFC\u81F4\u6D88\u606F\u4E0D\u4E00\u81F4</li></ol><p>rdt2.1:</p><p>\u89E3\u51B3\u540C\u6B65\u95EE\u9898 \u548C ACK/NAK \u51FA\u9519\uFF1A</p><ul><li>\u53D1\u9001\u7AEF\u53D1\u9001\u7684\u6570\u636E\u9664\u4E86\u6570\u636E\u548C checksum \u4E4B\u5916\u8FD8\u6709\u4E00\u4E2A\u5E8F\u5217\u53F7</li><li>\u5728\u63A5\u6536\u7AEF\u8FD4\u56DE ACK \u6216 NAK \u662F\uFF0C\u8FD8\u4F1A\u8FD4\u56DE\u5E8F\u5217\u53F7</li><li>\u53D1\u9001\u7AEF\u76F4\u5230\u6536\u5230\u6B63\u786E\u7684\u5E8F\u5217\u5316\u624D\u4F1A\u8F6C\u6362\u5230\u4E0B\u4E00\u4E2A\u72B6\u6001</li><li>\u662F\u9700\u8981 0 \u548C 1 \u4E24\u4E2A\u72B6\u6001\u5176\u5B9E\u5C31\u591F\u4E86\uFF0C\u533A\u5206\u65B0\u5305\u548C\u65E7\u5305</li></ul><p>rdt2.2:</p><p>\u53BB\u6389 NAK\uFF0C\u90FD\u7528 ACK \u8868\u793A\uFF1A</p><ul><li>\u53D1\u9001\u65B9\u53D1\u9001 ACK</li><li>\u63A5\u6536\u65B9\u63A5\u6536\u5230\u6570\u636E\uFF0C\u5982\u679C\u6B63\u786E\uFF0C\u8FD4\u56DE\u539F\u672C\u7684 ACK\uFF0C\u5982\u679C\u9519\u8BEF\uFF0C\u8FD4\u56DE\u5176\u4ED6\u6570\u636E\u8868\u793A\u9519\u8BEF\u3002</li><li>\u53D1\u9001\u65B9\u68C0\u67E5 ACK\uFF0C\u5982\u679C ACK \u76F8\u540C\uFF0C\u5219\u5207\u6362\u5230\u4E0B\u4E00\u4E2A\u72B6\u6001</li></ul><p>rdt3.0:</p><ol><li>\u53EF\u80FD\u53D1\u9001\u4E22\u5305 <ul><li>\u53D1\u9001\u7AEF\u7B49\u5F85\u4E00\u6BB5\u65F6\u95F4\uFF0C\u53EF\u80FD\u662F\u6570\u636E\u4E22\u4E86 \u6216\u8005 ACK \u4E22\u4E86\uFF0C\u65E0\u6CD5\u533A\u5206\uFF0C\u7EDF\u4E00\u91CD\u4F20</li><li>\u7B49\u5F85\u591A\u4E45\uFF0C\u51C6\u672C\u8BA1\u6570\u5668\uFF0C\u7B49\u5F85\u8D85\u65F6</li></ul></li></ol><h4 id="pipelined-protocols-\u6D41\u6C34\u7EBF" tabindex="-1"><a class="header-anchor" href="#pipelined-protocols-\u6D41\u6C34\u7EBF" aria-hidden="true">#</a> Pipelined protocols (\u6D41\u6C34\u7EBF)</h4><p>\u63D0\u9AD8\u6548\u7387\uFF0C\u5728\u53D1\u9001\u7AEF\u6536\u5230\u786E\u8BA4\u540E\u53EF\u4EE5\u53D1\u9001\u66F4\u591A\u6570\u636E</p><p>\u6BCF\u6B21\u53D1\u9001\u591A\u4EFD\u6570\u636E\uFF0C\u5F53\u4EFD\u6570\u63D0\u9AD8\u5230\u4E00\u5B9A\u7A0B\u5EA6\uFF0C\u5229\u7528\u7387\u5C31\u5230 100%, \u65E0\u6CD5\u7EE7\u7EED\u63D0\u9AD8\u6548\u7387</p><ol><li>go-Back-N</li></ol><p>\u6ED1\u52A8\u7A97\u53E3\uFF0C\u91CD\u4F20\u5F88\u591A</p><p>\u79EF\u7D2F\u786E\u8BA4\u673A\u5236</p><ul><li><p>\u53D1\u9001\u65B9\u53D1\u9001 5 \u4E2A\u6570\u636E\u5305</p></li><li><p>\u5047\u5982\u7B2C 3 \u4E2A\u4E22\u5931\uFF0C\u63A5\u6536\u65B9\u4F1A\u8FD4\u56DE\u9519\u8BEF\u7684\u5E8F\u53F7</p></li><li><p>\u53D1\u9001\u7AEF\u4ECE\u8FD9\u91CC\u91CD\u65B0\u53D1\u9001</p></li><li><p>\u5728\u63A5\u6536\u65B9</p></li><li><p>\u5982\u679C\u5230\u8FBE\u5206\u7EC4\u662F\u6309\u5E8F\u5230\u8FBE\uFF0C\u90A3\u4E48\u53D1\u9001 ACK\uFF0C\u53D1\u9001\u65B9\u6B63\u5E38\u79FB\u52A8\u7A97\u53E3</p></li><li><p>\u5982\u679C\u4E0D\u662F\u6309\u5E8F\u5230\u8FBE\uFF0C\u90A3\u4E48\u63A5\u6536\u65B9\u4E22\u5F03\u6240\u6709\u5931\u5E8F\u5206\u7EC4\uFF1B\u4E22\u5F03\u4E00\u4E2A\u6B63\u786E\u63A5\u6536\u7684\u5931\u5E8F\u5206\u7EC4\u53EF\u80FD\u4F1A\u5BFC\u81F4\u66F4\u591A\u7684\u91CD\u4F20</p></li><li><p>\u5982\u679C\u67D0\u4E2A\u786E\u8BA4\u4E22\u5931\uFF0C\u540E\u7EED\u7684\u786E\u8BA4\uFF08\u53EA\u6709\u53D1\u9001\u7AEF\u6B63\u786E\u63A5\u6536\u5230\u4E86\u6570\u636E\u624D\u53EF\u80FD\u4F1A\u6709\u540E\u7EED\u7684\u786E\u8BA4\uFF09\u4E5F\u80FD\u4F7F\u53D1\u9001\u7AEF\u6B63\u5E38\u79FB\u52A8\u7A97\u53E3</p></li></ul><ol start="2"><li>selective repeat(\u9009\u62E9\u91CD\u4F20)</li></ol><ul><li>\u63A5\u6536\u65B9\u56E0\u4E3A\u6709 buffer\uFF0C\u6240\u4EE5\u4E0D\u7528\u6309\u5E8F\u63A5\u6536\u5206\u7EC4\uFF0C\u5931\u5E8F\u7684\u5206\u7EC4\u4F1A\u88AB\u7F13\u5B58</li><li>\u53D1\u9001\u65B9\u7684 buffer\uFF0C\u6BCF\u4E2A\u6570\u636E\u5305\u90FD\u6709\u5404\u81EA\u7684\u8BA1\u6570\u5668\uFF0C\u4E0D\u7528\u91CD\u4F20\u591A\u7684\u6570\u636E\u5305</li></ul><p>\u4E3A\u4E86\u9632\u6B62\u8BEF\u5224\u6570\u636E\u5305</p><p>ACK \u7F16\u53F7\u6570\u91CF\u81F3\u5C11\u8981 = \u53D1\u9001\u7A97\u53E3 + \u63A5\u6536\u7A97\u53E3</p><h3 id="tcp" tabindex="-1"><a class="header-anchor" href="#tcp" aria-hidden="true">#</a> TCP</h3><p><img src="`+e+'" alt=""></p><ul><li>\u6E90\u7AEF\u53E3\u548C\u76EE\u7684\u7AEF\u53E3\u5B57\u6BB5\uFF1A\u5404\u5360 2 \u5B57\u8282\u3002\u7AEF\u53E3\u662F\u8FD0\u8F93\u5C42\u4E0E\u5E94\u7528\u5C42\u7684\u670D\u52A1\u63A5\u53E3\u3002\u8FD0\u8F93\u5C42\u7684\u590D\u7528\u548C\u5206\u7528\u529F\u80FD\u90FD\u8981\u901A\u8FC7\u7AEF\u53E3\u624D\u80FD\u5B9E\u73B0\u3002</li><li>\u5E8F\u53F7\u5B57\u6BB5\uFF1A\u5360 4 \u5B57\u8282\u3002TCP \u8FDE\u63A5\u4E2D\u4F20\u9001\u7684\u6570\u636E\u6D41\u4E2D\u7684\u6BCF\u4E00\u4E2A\u5B57\u8282\u90FD\u7F16\u4E0A\u4E00\u4E2A\u5E8F\u53F7\u3002\u5E8F\u53F7\u5B57\u6BB5\u7684\u503C\u5219\u6307\u7684\u662F\u672C\u62A5\u6587\u6BB5\u6240\u53D1\u9001\u7684\u6570\u636E\u7684\u7B2C\u4E00\u4E2A\u5B57\u8282\u7684\u5E8F\u53F7\u3002</li><li>\u786E\u8BA4\u53F7\u5B57\u6BB5\uFF1A\u5360 4 \u5B57\u8282\uFF0C\u662F\u671F\u671B\u6536\u5230\u5BF9\u65B9\u7684\u4E0B\u4E00\u4E2A\u62A5\u6587\u6BB5\u7684\u6570\u636E\u7684\u7B2C\u4E00\u4E2A\u5B57\u8282\u7684\u5E8F\u53F7\u3002</li><li>\u6570\u636E\u504F\u79FB\uFF08\u5373\u9996\u90E8\u957F\u5EA6\uFF09\uFF1A\u5360 4 \u4F4D\uFF0C\u5B83\u6307\u51FA TCP \u62A5\u6587\u6BB5\u7684\u6570\u636E\u8D77\u59CB\u5904\u8DDD\u79BB TCP \u62A5\u6587\u6BB5\u7684\u8D77\u59CB\u5904\u6709\u591A\u8FDC\u3002\u201C\u6570\u636E\u504F\u79FB\u201D\u7684\u5355\u4F4D\u662F 32 \u4F4D\u5B57\uFF08\u4EE5 4 \u5B57\u8282\u4E3A\u8BA1\u7B97\u5355\u4F4D\uFF09\u3002</li><li>\u4FDD\u7559\u5B57\u6BB5\uFF1A\u5360 6 \u4F4D\uFF0C\u4FDD\u7559\u4E3A\u4ECA\u540E\u4F7F\u7528\uFF0C\u4F46\u76EE\u524D\u5E94\u7F6E\u4E3A 0\u3002</li><li>\u7D27\u6025 URG\uFF1A\u5F53 URG \uF03D 1 \u65F6\uFF0C\u8868\u660E\u7D27\u6025\u6307\u9488\u5B57\u6BB5\u6709\u6548\u3002\u5B83\u544A\u8BC9\u7CFB\u7EDF\u6B64\u62A5\u6587\u6BB5\u4E2D\u6709\u7D27\u6025\u6570\u636E\uFF0C\u5E94\u5C3D\u5FEB\u4F20\u9001 (\u76F8\u5F53\u4E8E\u9AD8\u4F18\u5148\u7EA7\u7684\u6570\u636E)\u3002</li><li>\u786E\u8BA4 ACK\uFF1A\u53EA\u6709\u5F53 ACK \uF03D 1 \u65F6\u786E\u8BA4\u53F7\u5B57\u6BB5\u624D\u6709\u6548\u3002\u5F53 ACK \uF03D 0 \u65F6\uFF0C\u786E\u8BA4\u53F7\u65E0\u6548\u3002</li><li>\u63A8\u9001 PSH (PuSH)\uFF1A\u63A5\u6536 TCP \u6536\u5230 PSH = 1 \u7684\u62A5\u6587\u6BB5\uFF0C\u5C31\u5C3D\u5FEB\u5730\u4EA4\u4ED8\u63A5\u6536\u5E94\u7528\u8FDB\u7A0B\uFF0C\u800C\u4E0D\u518D\u7B49\u5230\u6574\u4E2A\u7F13\u5B58\u90FD\u586B\u6EE1\u4E86\u540E\u518D\u5411\u4E0A\u4EA4\u4ED8\u3002</li><li>\u590D\u4F4D RST (ReSeT) \uFF1A\u5F53 RST \uF03D 1 \u65F6\uFF0C\u8868\u660E TCP \u8FDE\u63A5\u4E2D\u51FA\u73B0\u4E25\u91CD\u5DEE\u9519\uFF08\u5982\u7531\u4E8E\u4E3B\u673A\u5D29\u6E83\u6216\u5176\u4ED6\u539F\u56E0\uFF09\uFF0C\u5FC5\u987B\u91CA\u653E\u8FDE\u63A5\uFF0C\u7136\u540E\u518D\u91CD\u65B0\u5EFA\u7ACB\u8FD0\u8F93\u8FDE\u63A5\u3002</li><li>\u540C\u6B65 SYN\uFF1A\u540C\u6B65 SYN = 1 \u8868\u793A\u8FD9\u662F\u4E00\u4E2A\u8FDE\u63A5\u8BF7\u6C42\u6216\u8FDE\u63A5\u63A5\u53D7\u62A5\u6587\u3002</li><li>\u7EC8\u6B62 FIN (FINis) \uFF1A\u7528\u6765\u91CA\u653E\u4E00\u4E2A\u8FDE\u63A5\u3002FIN \uF03D 1 \u8868\u660E\u6B64\u62A5\u6587\u6BB5\u7684\u53D1\u9001\u7AEF\u7684\u6570\u636E\u5DF2\u53D1\u9001\u5B8C\u6BD5\uFF0C\u5E76\u8981\u6C42\u91CA\u653E\u8FD0\u8F93\u8FDE\u63A5\u3002</li><li>\u7A97\u53E3\u5B57\u6BB5\uFF1A\u5360 2 \u5B57\u8282\uFF0C\u7528\u6765\u8BA9\u5BF9\u65B9\u8BBE\u7F6E\u53D1\u9001\u7A97\u53E3\u7684\u4F9D\u636E\uFF0C\u5355\u4F4D\u4E3A\u5B57\u8282\u3002</li><li>\u68C0\u9A8C\u548C\uFF1A\u5360 2 \u5B57\u8282\u3002\u68C0\u9A8C\u548C\u5B57\u6BB5\u68C0\u9A8C\u7684\u8303\u56F4\u5305\u62EC\u9996\u90E8\u548C\u6570\u636E\u8FD9\u4E24\u90E8\u5206\u3002\u5728\u8BA1\u7B97\u68C0\u9A8C\u548C\u65F6\uFF0C\u8981\u5728 TCP \u62A5\u6587\u6BB5\u7684\u524D\u9762\u52A0\u4E0A 12 \u5B57\u8282\u7684\u4F2A\u9996\u90E8\u3002</li><li>\u7D27\u6025\u6307\u9488\u5B57\u6BB5\uFF1A\u5360 16 \u4F4D\uFF0C\u6307\u51FA\u5728\u672C\u62A5\u6587\u6BB5\u4E2D\u7D27\u6025\u6570\u636E\u5171\u6709\u591A\u5C11\u4E2A\u5B57\u8282\uFF08\u7D27\u6025\u6570\u636E\u653E\u5728\u672C\u62A5\u6587\u6BB5\u6570\u636E\u7684\u6700\u524D\u9762\uFF09\u3002</li><li>\u9009\u9879\u5B57\u6BB5\uFF1A\u957F\u5EA6\u53EF\u53D8\u3002TCP \u6700\u521D\u53EA\u89C4\u5B9A\u4E86\u4E00\u79CD\u9009\u9879\uFF0C\u5373\u6700\u5927\u62A5\u6587\u6BB5\u957F\u5EA6 MSS\u3002MSS \u544A\u8BC9\u5BF9\u65B9 TCP\uFF1A\u201C\u6211\u7684\u7F13\u5B58\u6240\u80FD\u63A5\u6536\u7684\u62A5\u6587\u6BB5\u7684\u6570\u636E\u5B57\u6BB5\u7684\u6700\u5927\u957F\u5EA6\u662F MSS \u4E2A\u5B57\u8282\u3002</li></ul><p>TCP seq \u5E8F\u5217\u53F7</p>',76);function p(d,n){return r}var h=i(a,[["render",p]]);export{h as default};
